mapper.select:
select A.* from (select rownum as rnum , B.* from ( select * from boardtab
order by num desc ) B
where rownum <= #{end}) A where A.rnum >= #{start}

mapper.update:

      update boardtab set writer = #{writer},
	                     mail = #{mail},
	                     password = #{password},
	                     subject = #{subject},
	                     content = #{content}
                     where num = #{num}

update boardtab 
   set   hit = hit + 1
   where num = #{data}
